import React, { Component } from 'react'
// import PropTypes from 'prop-types'
import HomeJumbotron from "../components/HomeJumbotron"
import SocialIcons from "../components/SocialIcons"
import AboutMeHomeSection from "../components/AboutMeHomeSection"
import HomeSecondImage from "../components/HomeSecondImage"
import PortfolioHomeSection from "../components/PortfolioHomeSection"
import HomeThirdImage from "../components/HomeThirdImage"
import ContactMeHomeSection from "../components/ContactMeHomeSection"
import HomeFourthImage from "../components/HomeFourthImage"
import ActionButton from "../components/ActionButton"
import Foot from "../components/Foot"
import $ from "jquery"

export default class Home extends Component {
  // static propTypes = {
  //   prop: PropTypes
  // }
  constructor(props) {
    super(props);
    this.handleScroll = this.handleScroll.bind(this);
  }

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
  };

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  };

  handleScroll(event) {
    console.log('the scroll things')
    var scroll = $(window).scrollTop(); // how many pixels you've scrolled
        var os = $('#header-img').offsetTop; // pixels to the top of div1
        var ht = $('#header-img').height(); // height of div1 in pixels
        // if you've scrolled further than the top of div1 plus it's height
        // change the color. either by adding a class or setting a css property
        console.log(os);
        if(scroll > os + ht -100){
            $('#nav-bar').addClass('scroll-color');
            $('.nav-item, .navbar-brand').addClass('nav-item-scroll');
        } else {
            $('#nav-bar').removeClass('scroll-color');
            $('.nav-item, .navbar-brand').removeClass('nav-item-scroll');
        }
  };

  //smooth scrooling when you click the down arrow on the home page
  smoothScrollChevron = (event) => {
    event.preventDefault();
    var aTag = $("#down-arrow").attr("href")
    var offset = $(aTag).offset();
    console.log(offset);
    //.offset().top;
    $("html, body").animate({ scrollTop: offset.top - 150 }, 500);
  }

  render() {
    return (
      <div >
        <HomeJumbotron smoothScrollChevron={this.smoothScrollChevron} />
        <SocialIcons />
        <AboutMeHomeSection />
        <HomeSecondImage />
        <PortfolioHomeSection />
        <HomeThirdImage />
        <ContactMeHomeSection />
        <HomeFourthImage />
        <ActionButton />
        <Foot />
      </div>
    )
  }
}
