import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Home from "./pages/Home";
import Portfolio from "./pages/Portfolio";
import AboutMe from "./pages/AboutMe"
import NoMatch from "./pages/NoMatch";
import Nav from "./components/Nav";
import "./App.css"
import $ from "jquery"

class App extends Component {

  state = {
    navcolor: "#eb774c"
  }

  constructor(props) {
    super(props);
    this.handleScroll = this.handleScroll.bind(this);
  }

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
  };

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  };

  handleScroll(event) {
    console.log('the scroll things')
    // let $nav = $(".navbar")
    // var socialSpace = $("#home-top-space");
    // $nav.addClass('scroll-color', $(this).scrollTop() > socialSpace.height());
    var scroll = $(window).scrollTop(); // how many pixels you've scrolled
        var os = $('.header-img').offset().top; // pixels to the top of div1
        var ht = $('.header-img').height(); // height of div1 in pixels
        // if you've scrolled further than the top of div1 plus it's height
        // change the color. either by adding a class or setting a css property
        if(scroll > os + ht -100){
            $('.navbar').addClass('scroll-color');
            $('.navbar-item').addClass('nav-item-scroll');
            this.setState({
              navcolor: "white"
            })
        } else {
            $('.navbar').removeClass('scroll-color');
            $('.navbar-item').removeClass('nav-item-scroll');
            this.setState({
              navcolor: "#eb774c"
            })
        }
  };

  render() {
    return (
      <Router>
        <div>
          <Nav />
          <Switch>
            <Route exact path="/" component={Home} />
            <Route exact path="/home" component={Home} />
            <Route exact path="/portfolio" component={Portfolio} />
            <Route exact path="/about" component={AboutMe} />
            <Route component={NoMatch} />
          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
