import React, { Component } from 'react'
import PropTypes from 'prop-types'
import Nav from "../components/Nav";
import "./AboutMe.css"
import { useSpring, animated } from 'react-spring'

const calc = (x, y) => [x - window.innerWidth / 2, y - window.innerHeight / 2]
const trans1 = (x, y) => `translate3d(${x / 10}px,${y / 10}px,0)`
const trans2 = (x, y) => `translate3d(${x / 8 + 35}px,${y / 8 - 230}px,0)`
const trans3 = (x, y) => `translate3d(${x / 6 - 250}px,${y / 6 - 200}px,0)`
const trans4 = (x, y) => `translate3d(${x / 3.5}px,${y / 3.5}px,0)`

export default class AboutMe extends Component {
  static propTypes = {
    prop: PropTypes
  }

  render() {

    const [props, set] = useSpring(() => ({ xy: [0, 0], config: { mass: 10, tension: 550, friction: 140 } }))

    return (
      <div>
        <Nav navItemColor="#eb774c"/>
        {/* <div id="scene">
          <img data-depth="0.2" id="div1" className="images-par" src="https://cdn.pixabay.com/photo/2017/09/11/20/48/branches-2740419_1280.png" />
          <img data-depth="0.4" id="div2" src="https://cdn.pixabay.com/photo/2017/09/11/20/45/fir-2740412_1280.png" />
          <img data-depth="0.6" id="div3" src="https://cdn.pixabay.com/photo/2017/09/14/11/07/water-2748640_1280.png" />
        </div> */}
        <div className="container" onMouseMove={({ clientX: x, clientY: y }) => set({ xy: calc(x, y) })}>
      <animated.div className="card1" style={{ transform: props.xy.interpolate(trans1) }} />
      <animated.div className="card2" style={{ transform: props.xy.interpolate(trans2) }} />
      <animated.div className="card3" style={{ transform: props.xy.interpolate(trans3) }} />
      {/* <animated.div class="card4" style={{ transform: props.xy.interpolate(trans4) }} /> */}
    </div>
      </div>
    )
  }
}
