import React, { Component } from 'react'
import Nav from "../components/Nav";
import PortfolioHeader from "../components/PortfolioHeader"
import PortfolioProject from "../components/PortfolioProject"
import { Row, Col } from "react-materialize"
import Projects from "../utils/projects.js"
import Masonry from 'react-masonry-component';
import InViewMonitor from "react-inview-monitor"
import ActionButton from "../components/ActionButton"
import $ from "jquery"
import "./Portfolio.css"

var masonryOptions = {
  transitionDuration: 0
};

export default class Portfolio extends Component {

  constructor(props) {
    super(props);
    this.handleScroll = this.handleScroll.bind(this);
  }

  componentDidMount() {
    window.addEventListener('scroll', this.handleScroll);
    // let myProjects = JSON.stringify(Projects)

    this.setState({
      projectArr: Projects
    }, function () {
      // console.log(this.state.projectArr)
      // for (let i = 0; i < this.state.projectArr; i++) {
      //   console.log(this.state.projectArr[i])
      // }
    })
  }

  componentWillUnmount() {
    window.removeEventListener('scroll', this.handleScroll);
  };

  handleScroll(event) {

    var scroll = $(window).scrollTop(); // how many pixels you've scrolled
    var os = $('.project-area').offset().top; // pixels to the top of div1
    var ht = $('.project-area').height(); // height of div1 in pixels
    // if you've scrolled further than the top of div1 plus it's height
    // change the color. either by adding a class or setting a css property
    if (scroll > os + ht - 100) {
      $('.navbar').addClass('scroll-color');
      $('.navbar-item').addClass('nav-item-scroll');
      this.setState({
        navcolor: "white"
      })
    } else {
      $('.navbar').removeClass('scroll-color');
      $('.navbar-item').removeClass('nav-item-scroll');
      this.setState({
        navcolor: "#eb774c"
      })
    }

    // var scrollText = $(window).scrollTop(); // how many pixels you've scrolled
    // var osText = $('#header-text').offset().top; // pixels to the top of div1
    // //var ht = $('#header-text').height(); // height of div1 in pixels
    // // if you've scrolled further than the top of div1 plus it's height
    // // change the color. either by adding a class or setting a css property
    // if (scrollText > osText - (osText - 40)) {
    //   $('#header-text').addClass('scroll-disappear');
    //   // $("footer").addClass("footer-scroll");
    //   $("#down-arrow").addClass('scroll-disappear');
    //   // $("#footer-text-div").css("display", "block");
    // } else {
    //   $('#header-text').removeClass('scroll-disappear');
    //   // $("footer").removeClass("footer-scroll");
    //   $("#down-arrow").removeClass('scroll-disappear');
    //   // $("#footer-text-div").css("display", "block");
    // }
  };

  state = {
    projectArr: [],
    navcolor: "#eb774c"
  }



  render() {
    let childElements = this.state.projectArr.map(project => (
      <Col s={6}>
      <InViewMonitor
          classNameNotInView='vis-hidden'
          classNameInView='animated fadeInUpBig'
          intoViewMargin="5%"
        >
        <PortfolioProject projectData={project} />
      </InViewMonitor>
      </Col>
    ))

    return (

      <div>
        <Nav navItemColor="#eb774c" />
        <PortfolioHeader />
        <Row className="project-area" style={{ marginTop: "38vh"}}>
          <div>
            <h3 className="portfolio-quote"><em>"Talk is cheap. Show me the code."</em></h3>
            <h5 className="portfolio-quote-1"><b>- Linus Torvalds</b></h5>
          </div>
          <Col l={10} className="offset-l1">
          <Masonry
            className={'my-gallery-class'} // default ''
            elementType={'ul'} // default 'div'
            options={masonryOptions} // default {}
            disableImagesLoaded={false} // default false
            updateOnEachImageLoad={false} // default false and works only if disableImagesLoaded is false
          >
            {childElements}
          </Masonry>
          </Col>
        </Row>
        <ActionButton />
      </div>
    )
  }
}
